INSERT INTO `aboutUs_aboutuspage` VALUES (4,'[{\"id\": \"3b80f1f7-57b2-444f-9b7e-e1900bb8af94\", \"type\": \"hero_section_2\", \"value\": {\"layout\": \"hero_title_section_block\", \"hero_section_data\": 2}}, {\"id\": \"b0e2a6f1-2906-4195-8c48-5f14522ea80e\", \"type\": \"we_are_dbhomes_section\", \"value\": {\"image\": 31, \"content\": \"<p data-block-key=\\\"optls\\\">Với sứ mệnh kiến tạo không gian sống hạnh phúc, <b>DBHOMES</b> cam kết mang đến những giải pháp nội thất hoàn hảo, từ thiết kế đến thi công, giúp hiện thực hóa ước mơ và khát vọng của mỗi gia đình.<br/></p><p data-block-key=\\\"5m1b6\\\"><b>DBHOMES</b> luôn đặt sự hài lòng của khách hàng lên hàng đầu, cam kết mang đến trải nghiệm tối ưu với quy trình thi công chuyên nghiệp, đảm bảo thời gian hoàn thành nhanh chóng, chất lượng cao và chi phí hợp lý. Chúng tôi tin rằng <b>DBHOMES</b> xứng đáng là địa điểm đáng tin cậy để quý khách đặt trọn niềm tin.</p>\"}}, {\"id\": \"c1a05344-96df-4f88-877c-ef8d773fb708\", \"type\": \"about_us_section\", \"value\": {\"image\": 12, \"contents\": [{\"id\": \"824096a6-84e3-4ded-be50-2ef94d50db20\", \"type\": \"item\", \"value\": {\"title\": \"Sứ mệnh\", \"content\": \"<p data-block-key=\\\"qgksk\\\">Nâng tầm trải nghiệm sống bằng những giải pháp nội thất sáng tạo và tiên phong. Xây dựng đội ngũ gắn kết để đồng hành cùng khách hàng trên hành trình xây dựng tổ ấm hạnh phúc, kiến tạo không gian sống ấm áp, tiện nghi và tràn đầy yêu thương.</p>\"}}, {\"id\": \"706628c1-47b6-433f-9e33-96b7350cb1bf\", \"type\": \"item\", \"value\": {\"title\": \"Tôn chỉ mục đích\", \"content\": \"<p data-block-key=\\\"rnaya\\\">Trở thành đối tác đáng tin cậy, cung cấp giải pháp nội thất hàng đầu tại Việt Nam. Luôn tiên phong và đổi mới để mang đến trải nghiệm tốt nhất cho khách hàng. Cam kết phát triển bền vững, hướng đến lợi ích cộng đồng và bảo vệ môi trường.</p>\"}}], \"feature_items\": [{\"id\": \"c837f69f-ec08-43df-86ad-247483200eec\", \"type\": \"item\", \"value\": {\"number\": \"15+\", 