INSERT INTO `home_homepage` VALUES (3,'[{\"id\": \"e02892b4-d2d0-4bf2-9ce0-c6be16605bb9\", \"type\": \"hero_section_2\", \"value\": {\"layout\": \"hero_cta_slider_section_block\", \"hero_section_data\": 1}}, {\"id\": \"f598cd8e-1fd7-4fc0-9eb0-53756a95b9ab\", \"type\": \"hot_project_section\", \"value\": {\"content\": {\"title\": \"Hành trình kiến tạo không gian sống\", \"content\": \"<p data-block-key=\\\"ajmt5\\\">Những căn hộ và ngôi nhà nổi bật, nơi thiết kế nội thất hòa quyện giữa thẩm mỹ</p><p data-block-key=\\\"398vp\\\">và công năng, tạo nên tổ ấm khác biệt, đậm chất riêng.</p>\"}, \"projects\": [{\"id\": \"95001b2c-cdf9-409c-b13f-ef97c3e075bc\", \"type\": \"item\", \"value\": 57}, {\"id\": \"156ffdcc-0011-4eb5-ad9a-1dedd6349480\", \"type\": \"item\", \"value\": 58}, {\"id\": \"1d8b39e3-b5b0-4451-a287-3d906cdb1fe2\", \"type\": \"item\", \"value\": 61}, {\"id\": \"0c34516c-9e6b-4079-82d8-019869f8d71f\", \"type\": \"item\", \"value\": 67}, {\"id\": \"52c8f964-6581-457e-bec6-a9b82247fc48\", \"type\": \"item\", \"value\": 70}]}}, {\"id\": \"d86740df-9d86-4f55-9663-1c0d96591c43\", \"type\": \"about_us_section\", \"value\": {\"buttons\": [{\"id\": \"0bf54588-9b3e-4dee-9f38-67e7749fecf6\", \"type\": \"item\", \"value\": {\"url\": \"https://genk.vn\", \"page\": 4, \"label\": \"Xem thêm\"}}, {\"id\": \"07bd9628-14f8-4af7-9a46-300a9ee063c2\", \"type\": \"item\", \"value\": {\"url\": \"http://testing.dbhomes.com.vn:8000/#contact-section\", \"page\": null, \"label\": \"Liên hệ ngay\"}}], \"content\": \"<p data-block-key=\\\"bxg2w\\\">Với sứ mệnh kiến tạo không gian sống hạnh phúc, DBHOMES cam kết mang đến những giải pháp nội thất hoàn hảo, từ thiết kế đến thi công, giúp hiện thực hóa ước mơ và khát vọng của mỗi gia đình.</p><p data-block-key=\\\"c0okj\\\">DBHOMES luôn đặt sự hài lòng của khách hàng lên hàng đầu, cam kết mang đến trải nghiệm tối ưu với quy trình thi công chuyên nghiệp, đảm bảo thời gian hoàn thành nhanh chóng, chất lượng cao và chi phí hợp lý. Chúng tôi tin rằng DBHOMES xứng đáng là địa điểm đáng tin cậy để quý khách đặt trọn niềm tin.</p>\", \"background\": {\"image\": 9, \"alt_text\": \"\"}, \"feature_items\": [{\"id\": \"e73dccaa-ad2e-402a-8a14-def7b7a577d8\", \"type\": \"item\", \"value\": {\"number\": \"15+\", \"description\": \"Số năm kinh nghiệm\"}}, {\"id\": \"5d9940ea-bbdf-4328-ba37-e7eee548795c\", \"type\": \"item\", \"value\": {\"number\": \"100+\", \"description\": \"Số lượng dự án\"}}, {\"id\": \"cdd3bcf0-ea61-4f25-8fc1-b097d5531f67\", \"type\": \"item\", \"value\": {\"number\": \"50+\", \"description\": \"Số lượng nhân sự\"}}, {\"id\": \"a06c8e2f-2d74-4c02-99d4-8a0185754ab2\", \"type\": \"item\", \"value\": {\"number\": \"5000 m²\", \"description\": \"Diện tích nhà xưởng\"}}]}}, {\"id\": \"ca993f4e-b8bc-4365-96db-eed85969e3a3\", \"type\": \"partner_section\", \"value\": {\"brands\": [{\"id\": \"abf2be35-a637-4f08-afed-27fd3ad33e8d\", \"type\": \"item\", \"value\": {\"link\": \"https://vinhomes-grandpark.com.vn/\", \"image\": 21}}, {\"id\": \"b03cd832-d0d4-4105-a4e5-d07014bb9477\", \"type\": \"item\", \"value\": {\"link\": \"https://www.capitaland.com/vn/vi.html?viewmode=mapview\", \"image\": 76}}, {\"id\": \"eae71fe1-33b9-4488-b469-24971ffb924e\", \"type\": \"item\", \"value\": {\"link\": \"https://masterisehomes.com/\", \"image\": 77}}, {\"id\": \"0c70ae13-c3d2-4b39-ade4-a7b2935e9563\", \"type\": \"item\", \"value\": {\"link\": \"https://sonkimland.vn/\", \"image\": 78}}, {\"id\": \"3646ed40-ba73-4e30-80db-d88900cb865d\", \"type\": \"item\", \"value\": {\"link\": \"https://phumyhung.vn/\", \"image\": 79}}, {\"id\": \"3d96882b-a982-4804-88d8-090182939217\", \"type\": \"item\", \"value\": {\"link\": \"https://masterisehomes.com/\", \"image\": 77}}, {\"id\": \"305b9123-251f-4cc9-b8db-e50b5d7ee206\", \"type\": \"item\", \"value\": {\"link\": \"https://sonbetongconpa.vn/\", \"image\": 80}}], \"main_media\": {\"video_url\": \"\", \"video_file\": 1}}}, {\"id\": \"6bf42dec-7e46-4f0c-90af-9309b2c00e5c\", \"type\": \"services_section\", \"value\": {\"title\": \"Kiến tạo không gian sống hoàn hảo,\\r\\ntừ thiết kế đến thi công\", \"services\": [{\"id\": \"aede82f8-158c-4ed6-bf75-706779970a6a\", \"type\": \"item\", \"value\": {\"icon\": 23, \"title\": \"Thiết kế & Thi công nội thất\", \"description\": \"<p data-block-key=\\\"0gq2z\\\">Từ ý tưởng đến hoàn thiện – đảm bảo sự đồng bộ trong phong cách, tối ưu công năng và thẩm mỹ cho từng không gian sống.</p>\", \"hover_image\": 24}}, {\"id\": \"01a3f846-a4d6-4df7-91b2-ce6fae0f17c2\", \"type\": \"item\", \"value\": {\"icon\": 25, \"title\": \"Thi công nội thất\", \"description\": \"<p data-block-key=\\\"959u3\\\">Cam kết sử dụng vật liệu đúng mẫu, kỹ thuật chuẩn xác và kiểm soát chất lượng nghiêm ngặt – đảm bảo chất lượng và thẩm mỹ.</p>\", \"hover_image\": 24}}, {\"id\": \"276036e6-dc51-4143-8632-bf7ee1d70864\", \"type\": \"item\", \"value\": {\"icon\": 26, \"title\": \"Cung cấp nội thất\", \"description\": \"<p data-block-key=\\\"959u3\\\">Cung cấp - vận chuyển - lắp đặt hoàn thiện nội thất rời hoặc đóng theo yêu cầu, được sản xuất theo tiêu chuẩn cao và đồng bộ.</p>\", \"hover_image\": 24}}], \"description\": \"<p data-block-key=\\\"65pqn\\\">Chúng tôi cung cấp dịch vụ thiết kế, thi công và lắp đặt nội thất tối giản, hiện đại, mang lại không gian sống hoàn hảo và tiện nghi.</p>\"}}, {\"id\": \"5bbd790a-c9c5-436a-93fd-37621aae1414\", \"type\": \"technology_section\", \"value\": {\"chplay_url\": \"https://play.google.com\", \"appstore_url\": \"https://apps.apple.com\", \"block1_image\": 19, \"block2_image\": 20, \"block2_paragraph\": \"<p data-block-key=\\\"vht0c\\\">Ứng dụng theo dõi tiến độ mang lại sự minh bạch tối đa. Mọi trao đổi, giám sát và thông tin đều được lưu trữ ngay trên ứng dụng. Bạn dễ dàng theo dõi mọi bước đi của căn nhà tương lai, mọi lúc mọi nơi.</p>\", \"block1_paragraph1\": \"<p data-block-key=\\\"l552k\\\">Ứng dụng theo dõi tiến độ DBHOMES giúp bạn theo sát tiến trình hiện thực hóa tổ ấm mơ ước một cách dễ dàng, minh bạch và tiện lợi.</p><p data-block-key=\\\"e4rv9\\\">Bạn có thể cập nhật mọi công đoạn thi công, trao đổi trực tiếp với đội ngũ chuyên môn của dự án, và lưu trữ tất cả hồ sơ công trình ngay trên điện thoại – dễ dàng sử dụng từ DBVerse.</p>\", \"block1_title_main\": \"Theo dõi dự án trong lòng bàn tay\", \"block2_title_main\": \"Cập nhật thông tin dự án mỗi ngày\", \"block1_title_highlight\": \"DB.Verse\", \"block2_title_highlight\": \"DB.Verse\"}}, {\"id\": \"d78d8f96-8422-445c-b3cd-14ae9a1cc79b\", \"type\": \"ecosystem_section\", \"value\": {\"title\": \"Hệ sinh thái DBGROUP\", \"description\": \"<p data-block-key=\\\"5u1tr\\\">Chúng tôi cung cấp giải pháp toàn diện cho không gian nội thất từ khách hàng doanh nghiệp đến cá nhân, từ thiết kế - thi công trọn gói đến cung cấp nội thất rời, mang đến trải nghiệm dịch vụ tối ưu nhất cho khách hàng.</p>\"}}, {\"id\": \"72a16f26-667c-4777-9230-273a605953ba\", \"type\": \"customer_feedback_block\", \"value\": {\"feedbacks\": [{\"id\": \"cf34d5d9-3b03-4756-8fed-370eafa4bddd\", \"type\": \"item\", \"value\": {\"customer_name\": \"Chị Liên\", \"customer_avatar\": 65, \"customer_project\": \"Dự án Beverly Solari\", \"customer_feedback\": \"<p data-block-key=\\\"njlhl\\\">Dịch vụ chăm sóc khách hàng của DBHOMES rất tốt, đánh giá 9/10. Chị cảm ơn bạn Tâm đã theo dự án rất nhiệt tình.</p>\"}}, {\"id\": \"d3e0e7e1-4b60-4f10-afbe-7b8d11a6f1de\", \"type\": \"item\", \"value\": {\"customer_name\": \"Chị Liên\", \"customer_avatar\": 65, \"customer_project\": \"Dự án Beverly Solari\", \"customer_feedback\": \"<p data-block-key=\\\"njlhl\\\">Dịch vụ chăm sóc khách hàng của DBHOMES rất tốt, đánh giá 9/10. Chị cảm ơn bạn Tâm đã theo dự án rất nhiệt tình.</p>\"}}, {\"id\": \"16d8e2ff-1925-4d1f-91e5-1a00bebd3e83\", \"type\": \"item\", \"value\": {\"customer_name\": \"Anh Công Thành & Chị Minh Hà\", \"customer_avatar\": 66, \"customer_project\": \"Dự án Cardinal Court Phú Mỹ Hưng\", \"customer_feedback\": \"<p data-block-key=\\\"cka1v\\\">Hài lòng với DBHOMES từ lúc thiết kế đến lúc bàn giao, bao gồm thiết kế, màu sắc và chất lượng đều đạt được yêu cầu của anh chị. Chọn DBHOMES vì trong nhiều đối tác, mình cảm nhận được sự an tâm, tin cậy.</p>\"}}, {\"id\": \"3dcc54f0-8d03-4c55-8f6f-0e5c882af331\", \"type\": \"item\", \"value\": {\"customer_name\": \"Chị Thu Hằng\", \"customer_avatar\": 67, \"customer_project\": \"Dự án Lumiere Boulevard\", \"customer_feedback\": \"<p data-block-key=\\\"cka1v\\\">Vô cùng vui sướng là những gì mình hình dung trong đầu đều được DBHOMES lên ý tưởng và biến thành hiện thực.</p>\"}}, {\"id\": \"bd62ee6f-6823-4ff5-ac1f-c7b1520e0d56\", \"type\": \"item\", \"value\": {\"customer_name\": \"Chị Thu Hằng\", \"customer_avatar\": 67, \"customer_project\": \"Dự án Lumiere Boulevard\", \"customer_feedback\": \"<p data-block-key=\\\"cka1v\\\">Vô cùng vui sướng là những gì mình hình dung trong đầu đều được DBHOMES lên ý tưởng và biến thành hiện thực.</p>\"}}]}}, {\"id\": \"809596e7-188d-4bea-8ca0-d7a685c2a9c6\", \"type\": \"HotNews\", \"value\": {\"news_list\": [{\"id\": \"8c8c6c62-bbe0-4782-a55a-3cabee8cac09\", \"type\": \"item\", \"value\": 56}, {\"id\": \"883fcd65-55f9-4e2b-a09a-2458fcc3bbfc\", \"type\": \"item\", \"value\": 28}, {\"id\": \"a113ecbb-a84e-4ec4-bcd7-f0d81a3c4b63\", \"type\": \"item\", \"value\": 34}]}}]','',89);
